         p.isCanCancel = isCanCancel;
         return builder;
     }
     public ConcreteBuilder leftBt(String text, ButtonClickLister lister) {
         p.leftButtonText = text;
         p.leftListener = lister;
         return builder;
     }
     public ConcreteBuilder leftBtColor(int color) {
